mapper.select:
SELECT order_id, order_date, request_date, c_order.note, quantity, unit_price, unit, c_order.image, file, c_order.status, custom.custom_id, custom_name, product.product_id, product_name FROM c_order LEFT JOIN custom ON c_order.custom_id = custom.custom_id LEFT JOIN product ON c_order.product_id = product.product_id
and status=#{status}
ORDER BY c_order.order_date

mapper.update:
UPDATE c_order
SET status = #{status}
WHERE
	order_id IN

	#{id}

UPDATE c_order
SET note = #{note}
WHERE
	order_id = #{orderId,jdbcType=VARCHAR}

mapper.insert:
insert into c_order (order_id, custom_id, product_id, 
  order_date, request_date, note, 
  quantity, unit_price, unit, 
  image, file, status
  )
values (#{orderId,jdbcType=VARCHAR}, #{customId,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}, 
  #{orderDate,jdbcType=TIMESTAMP}, #{requestDate,jdbcType=TIMESTAMP}, #{note,jdbcType=VARCHAR}, 
  #{quantity,jdbcType=INTEGER}, #{unitPrice,jdbcType=DECIMAL}, #{unit,jdbcType=VARCHAR}, 
  #{image,jdbcType=VARCHAR}, #{file,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}
  )
